import styles from './Auth.module.css'
import Button from "../../components/button/Button";
import {NavLink, useLocation} from "react-router-dom";
import {SIGN_IN_ROUTE} from "../../utils/route_constants";
const Auth = () => {
    const location = useLocation()
    const signIn = location.pathname == SIGN_IN_ROUTE;
    return (
        <div className={styles.auth}>
            <div className={styles.container}>
                Auth
                <input type={'text'} placeholder={'email'}/>
                <input type={'text'} placeholder={'password'}/>
                <div className={styles.btns}>
                    <div >Already have an <NavLink className='border-b-4 border-blue-50 rounded-b-md hover:border-red-50 duration-700 ' to={'/'}>account?</NavLink></div>
                    <Button>Auth</Button>
                </div>
            </div>
        </div>
    );
};

export default Auth;
