import React from 'react';
import styles from './Items.module.css'
import star from '../../../assets/star.png'
import {Navigate} from "react-router-dom";

const Items = ({items}) => {
    return (
        <div className={styles.container}>
            {items.map(item => (
                <div onClick={() => <Navigate to={`/${item.id}`}/>}
                    key={item.id} className={styles.item}>
                    <img className={'w-[190px] h-[190px] '} src={item.img}/>
                    <div className={'flex flex-row'}>
                        <div className={'text-gray-500 mr-6'}>{item.name}</div>
                        <div className={'items-center'}>
                            <div>
                                Rating: {item.rating}
                            </div>
                            <img className={'ml-2 w-[15px] h-[15px] items-center'} src={star}/>
                        </div>
                    </div>
                    <div>Price: {item.price}$</div>
                </div>
            ))}

        </div>
    );
};

export default Items;
