import React, {useContext, useState} from 'react';
import Button from "../button/Button";
import Modal from "../Modal";
import {Context} from "../../App";


const CreateItem = ({onHide, show}) => {
    const {devices} = useContext(Context)
    const [name, setName] = useState('')
    const [price, setPrice] = useState('')
    const [file, setFile] = useState()
    const [brand, setBrand] = useState(0)
    const [type, setType] = useState(0)
    const [info, setInfo] = useState([{id: new Date(), title: '', description: ''}])

    const deleteInfo = (id) => {
        setInfo(...info.filter(item => item.id !== id));
    }

    return (
        <Modal onHide={onHide} show={show}>
            <div className={'mb-8'}>Create Item</div>
            <input className={'p-2 m-4 rounded-2xl'}
                   placeholder={'Item name'}
                   value={name}
                   onChange={e => setName(e.target.value)}/>
            <input className={'p-2 m-4 rounded-2xl'}
                   placeholder={'Item price'}
                   value={price}
                   onChange={e => setPrice(e.target.value)}/>
            <input className={'p-2 m-4 rounded-2xl'} type={'file'}
                   onChange={e => setFile(e.target.files[0])}/>
            <div className={'p-2 m-4 rounded-2xl'}>
                <select
                    className={'p-4 border-none w-2/3'}
                    onChange={e => setType(e.target.value)}>
                    {devices.types.map(type => {
                        return <option value={type.id}>{type.name}</option>
                    })}
                </select>
            </div>
            <div className={'p-2 m-4'}>
                <select className={'p-4 border-none w-2/3'}
                    onChange={e => setBrand(e.target.value)}>
                    {devices.brands.map(brand => {
                        return <option value={brand.id}>{brand.name}</option>
                    })}
                </select>
            </div>
            <div>
                <div>Characteristics</div>
                <div>{info.map(item => {
                    return <div>{item.title}: {item.description}
                        <div onClick={() => deleteInfo(item.id)}>X</div>
                    </div>
                })}</div>
            </div>
            <input type={'text'} placeholder={'Info title'} onChange={e => {
                const item = info.pop();
                item.title = e.target.value;
                setInfo([...info, item])
            }}/>
            <input type={'text'} placeholder={'Info description'} />
            <Button onClick={() => setInfo(...info, {id: new Date(), title: '', description: ''})}>Add characteristic</Button>
            <Button>Create item</Button>
        </Modal>
    );
};

export default CreateItem;
