import React from 'react';
import styles from './ItemPage.module.css'
import star from '../../assets/star.png'

const ItemPage = () => {
    const item = {id: 1, name: 'example', price: 1000, rating: 5, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYz2huNWs1qzjomU38a88RbCqDoddasQv6Gw&usqp=CAU'};
    return (
        <div className={styles.container}>
            <div className={styles.view}>
                <div>
                    <img className={'w-full h-full'} src={item.img} />
                </div>
                <div className={styles.rate}>
                    <div className={'text-center'}>{item.name}</div>
                    <div className={'absolute flex justify-center top-1/3 text-6xl'}>{item.rating}</div>
                    <img className={'bg-cover'} src={star} />
                </div>
                <div className={styles.price}>
                    <h3>Starts with {item.price}</h3>
                    <button className={'w-fit border-2 p-4 rounded-2xl'}>Add to cart</button>
                </div>
            </div>
            <div>
                <div className={'font-9xl'}>
                    Characteristics
                </div>
            </div>
        </div>
    );
};

export default ItemPage;
